            <p>{description}</p>
            <p>
              <b>Precio: ${price}</b>
            </p>
          </div>
          <span
            to="/"
            className="btn-floating halfway-fab waves-effect waves-light red"
            onClick={() => {
              handleClick(data);
              products.push(data)

            }}
          >
            <a href="#" className="btn btn-primary">
